                    touched: false,
                    validationRules: {
                        isRequired: true
                    }
                },
                newPwdAgain: {
                    value: '',
                    name: 'newPwdAgain',
                    type: 'password',
                    label: 'Yeni Şifreniz Tekrar',
                    placeholder: 'Yeni şifreniz Tekrar',
                    error: 'Lütfen yeni şifrenizi girin.',
                    valid: false,
                    touched: false,
                    validationRules: {
                        isRequired: true
                    }
